package JavaBasics;

public class DataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*bdnsbcs
		sxssdc
		scxscsdcsd*/
		
		int i=0;
		int p=23;
		int y= -9;
		
		double d =34;
		double f=45.67;
		
		char c='r';
		char u='2';
		char o='%';
		
		boolean b=true;
		boolean t = false;
		//int,double,char,boolean are primitive data types
		
		//String is a class and not data type
		String s1= "sneha";
		String s2="2000";
		
		System.out.println(b);
		System.out.println(f);
		System.out.println(c+u+o);
		System.out.println(s1+s2);
		System.out.println(b);
		System.out.println(p+u);
		System.out.println(p+s1);
		System.out.println(i+d);
		System.out.println(d+s1); 
		}

}
